class Solution:
    # @param A : list of integers
    # @return a list of integers
    def solve(self, A):
        positive_list, negative_list = list(), list()

        for i in A:
            if i < 0:
                negative_list.append(i)
            else:
                positive_list.append(i)
        if len(positive_list) == 0 or len(negative_list) == 0:
            return A
        result = []
        count = 0

        while count < len(A)-1:
            if count <= len(negative_list)-1:
                result.append(negative_list[count])
            if count <= len(positive_list)-1:
                result.append(positive_list[count])
            count += 1
        print(len(result))


